import { Component, OnInit } from '@angular/core';

import { userDataList } from '../Modals/Modals';
import { HttpCallService } from 'src/services/http.service.';
import { Router } from '@angular/router';
import {
  FormBuilder,
  FormGroup,
  FormControl,
  Validators,
} from '@angular/forms';

@Component({
  selector: 'app-prod-list',
  templateUrl: './prod-list.component.html',
  styleUrls: ['./prod-list.component.css'],
})
export class ProdListComponent implements OnInit {
  constructor(
    public restApi: HttpCallService,
    private router: Router,
    private formBuilder: FormBuilder
  ) {}

  userData: userDataList[] = [];
  url: string = 'https://652e8c840b8d8ddac0b194c7.mockapi.io/admin/userData';

  editingUserId: number | null = null;
  editedUser: userDataList = {} as userDataList;
  newUser: userDataList = {} as userDataList;

  isAddUserFormVisible: boolean = false;

  form: FormGroup = new FormGroup({
    name: new FormControl(''),
    lastName: new FormControl(''),
    email: new FormControl(''),
    gender: new FormControl(''),
    birthDate: new FormControl(''),
  });

  ngOnInit() {
    this.getData();

    this.form = this.formBuilder.group({
      name: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.maxLength(20),
        ],
      ],
      lastName: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.maxLength(20),
        ],
      ],
      email: ['', [Validators.required, Validators.email]],

      gender: ['', [Validators.required, Validators.required]],
      birthDate: ['', [Validators.required]],
    });
  }

  addUser() {
    this.isAddUserFormVisible = !this.isAddUserFormVisible;
  }

  addNewUser() {
    this.restApi.post(this.url, this.newUser).subscribe((response: any) => {
      this.userData.push(response);
      this.getData();
      this.newUser = {
        email: '',
        id: 0,
        jenisKelamin: '',
        namaBelakang: '',
        namaDepan: '',
        password: '',
        tanggalLahir: 1,
      };
      this.addUser();
    });
  }

  getData() {
    this.restApi.get(this.url).subscribe((data: any) => {
      this.userData = data;
    });
  }

  editUser(id: number) {
    this.editingUserId = id;
    const userToEdit = this.userData.find((user) => user.id === id);
    if (userToEdit !== undefined) {
      this.editedUser = userToEdit;
    } else {
      null;
    }
  }

  saveUser() {
    this.restApi
      .put(`${this.url}/${this.editingUserId}`, this.editedUser)
      .subscribe(() => {
        this.getData();
        this.editingUserId = null;
      });
  }

  cancelEdit() {
    this.editingUserId = null;
  }

  deleteUser(id: number) {
    const userIndex = this.userData.findIndex((u) => u.id === id);
    if (userIndex !== -1) {
      const userToDelete = this.userData[userIndex];
      this.restApi.delete(`${this.url}/${userToDelete.id}`).subscribe(() => {
        this.userData.splice(userIndex, 1);
      });
    }
  }
}
<app-navbar></app-navbar>

<div class="wrapper-card">
  <button class="add-button" (click)="addUser()">Add</button>

  <div *ngFor="let user of userData" class="user-card">
    <h2>
      {{ user.namaDepan }} <span>{{ user.namaBelakang }}</span>
    </h2>
    <div class="user-details">
      <p>Email: {{ user.email }}</p>
      <p>Jenis Kelamin: {{ user.jenisKelamin }}</p>
    </div>
    <p>Tanggal Lahir: {{ user.tanggalLahir | date }}</p>
    <button class="edit-button" (click)="editUser(user.id)">Edit</button>
    <button class="delete-button" (click)="deleteUser(user.id)">Delete</button>
  </div>

  <div *ngIf="editingUserId !== null" class="edit-wrapper">
    <div class="edit-user-form">
      <h2>Edit User</h2>
      <form (submit)="saveUser()" [formGroup]="form">
        <!-- Create form inputs for editing user details -->
        <label for="editedName">First Name:</label>
        <input
          formControlName="name"
          type="text"
          id="editedName"
          [(ngModel)]="editedUser.namaDepan"
          name="namaDepan"
          required
        />
        <div *ngIf="form.get('name')?.hasError('required')">
          <span class="error-message">name is required.</span>
        </div>
        <div *ngIf="form.get('name')?.hasError('minlength')">
          <span class="error-message"
            >Name must be at least 6 characters long.</span
          >
        </div>

        <label for="editedLastName">Last Name:</label>
        <input
          type="text"
          formControlName="lastName"
          id="editedLastName"
          [(ngModel)]="editedUser.namaBelakang"
          name="namaBelakang"
          required
        />
        <div *ngIf="form.get('lastName')?.hasError('required')">
          <span class="error-message">Last name is required.</span>
        </div>
        <div *ngIf="form.get('lastName')?.hasError('minlength')">
          <span class="error-message"
            >Last name must be at least 6 characters long.</span
          >
        </div>

        <label for="editedEmail">Email:</label>
        <input
          type="email"
          formControlName="email"
          id="editedEmail"
          [(ngModel)]="editedUser.email"
          name="email"
          required
        />
        <div *ngIf="form.get('email')?.hasError('email')">
          <span class="error-message">Must be in email format.</span>
        </div>

        <label for="editedGender">Gender:</label>
        <input
          type="text"
          formControlName="gender"
          id="editedGender"
          [(ngModel)]="editedUser.jenisKelamin"
          name="jenisKelamin"
          required
        />
        <div *ngIf="form.get('gender')?.hasError('required')">
          <span class="error-message">Gender is required.</span>
        </div>

        <label for="editedBirthdate">Birthdate:</label>
        <input
          type="date"
          id="editedBirthdate"
          [(ngModel)]="editedUser.tanggalLahir"
          name="tanggalLahir"
          required
        />
        <div *ngIf="form.get('birthDate')?.hasError('required')">
          <span class="error-message">Birth Date is required.</span>
        </div>

        <button type="submit">Save</button>
        <button (click)="cancelEdit()">Cancel</button>
      </form>
    </div>
  </div>

  <!-- Add User Form -->
  <div *ngIf="isAddUserFormVisible" class="add-user-form-wrapper">
    <div class="add-user-form">
      <h2>Add User</h2>
      <form (submit)="addNewUser()">
        <label for="newFirstName">First Name:</label>
        <input
          type="text"
          id="newFirstName"
          [(ngModel)]="newUser.namaDepan"
          name="namaDepan"
          required
        />

        <label for="newLastName">Last Name:</label>
        <input
          type="text"
          id="newLastName"
          [(ngModel)]="newUser.namaBelakang"
          name="namaBelakang"
          required
        />

        <label for="newEmail">Email:</label>
        <input
          type="email"
          id="newEmail"
          [(ngModel)]="newUser.email"
          name="email"
          required
        />

        <label for="newGender">Gender:</label>
        <input
          type="text"
          id="newGender"
          [(ngModel)]="newUser.jenisKelamin"
          name="jenisKelamin"
          required
        />

        <label for="newBirthdate">Birthdate:</label>
        <input
          type="date"
          id="newBirthdate"
          [(ngModel)]="newUser.tanggalLahir"
          name="tanggalLahir"
          required
        />

        <button type="submit">Add User</button>
        <button type="button" class="cancelButton" (click)="addUser()">
          Cancel
        </button>
      </form>
    </div>
  </div>
</div>
