import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpCallService } from 'src/services/http.service.';
import { prodDataList } from '../Modals/Modals';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css'],
})
export class UserListComponent {
  constructor(public restApi: HttpCallService, private router: Router) {}

  prodData: prodDataList[] = [];
  url: string = 'https://652e8e2a0b8d8ddac0b197f5.mockapi.io/test/List';

  editingprodId: number | null = null;
  editedprod: prodDataList = {} as prodDataList;
  newprod: prodDataList = {} as prodDataList;

  isAddprodFormVisible: boolean = false;

  ngOnInit() {
    this.getData();
  }

  addprod() {
    this.isAddprodFormVisible = !this.isAddprodFormVisible;
  }

  addNewprod() {
    this.restApi.post(this.url, this.newprod).subscribe((response: any) => {
      this.prodData.push(response);
      this.getData();
      this.newprod = {
        id: 0,
        name: '',
        description: '',
        gambar: '',
        category: '',
        stock: 0,
      };
      this.addprod();
    });
  }

  getData() {
    this.restApi.get(this.url).subscribe((data: any) => {
      this.prodData = data;
      console.log(this.prodData);
    });
  }

  editprod(id: number) {
    this.editingprodId = id;
    const prodToEdit = this.prodData.find((prod) => prod.id === id);
    if (prodToEdit !== undefined) {
      this.editedprod = prodToEdit;
    } else {
      null;
    }
  }

  saveprod() {
    this.restApi
      .put(`${this.url}/${this.editingprodId}`, this.editedprod)
      .subscribe(() => {
        this.getData();
        this.editingprodId = null;
      });
  }

  cancelEdit() {
    this.editingprodId = null;
  }

  deleteprod(id: number) {
    const prodIndex = this.prodData.findIndex((u) => u.id === id);
    if (prodIndex !== -1) {
      const prodToDelete = this.prodData[prodIndex];
      this.restApi.delete(`${this.url}/${prodToDelete.id}`).subscribe(() => {
        this.prodData.splice(prodIndex, 1);
      });
    }
  }
}
<app-navbar></app-navbar>

<div class="wrapper-card">
  <button class="add-button" (click)="addprod()">Add</button>

  <div *ngFor="let prod of prodData" class="prod-card">
    <div class="prod-details">
      <p>Name: {{ prod.name }}</p>
      <p>Description: {{ prod.description }}</p>

      <img src="{{ prod.gambar }}" alt="gambar" width="200" height="200" />
      <p>category: {{ prod.category }}</p>
      <p>Stock: {{ prod.stock }}</p>
    </div>

    <button class="edit-button" (click)="editprod(prod.id)">Edit</button>
    <button class="delete-button" (click)="deleteprod(prod.id)">Delete</button>
  </div>

  <div *ngIf="editingprodId !== null" class="edit-wrapper">
    <h2>Edit Product</h2>
    <form (submit)="saveprod()">
      <!-- Create form inputs for editing prod details -->
      <label for="editedName">Name:</label>
      <input
        type="text"
        id="editedName"
        [(ngModel)]="editedprod.name"
        name="namaDepan"
        required
      />

      <label for="editedBirthdate">Description:</label>
      <input
        type="text"
        id="editedBirthdate"
        [(ngModel)]="editedprod.description"
        name="tanggalLahir"
        required
      />

      <button type="submit">Save</button>
      <button (click)="cancelEdit()">Cancel</button>
    </form>
  </div>

  <!-- Add prod Form -->
  <div *ngIf="isAddprodFormVisible" class="add-prod-form-wrapper">
    <div class="add-prod-form">
      <h2>Add Product</h2>
      <form (submit)="addNewprod()">
        <label for="newFirstName">Name:</label>
        <input
          type="text"
          id="newFirstName"
          [(ngModel)]="newprod.name"
          name="namaDepan"
          required
        />

        <label for="newLastName">Description:</label>
        <input
          type="text"
          id="newLastName"
          [(ngModel)]="newprod.description"
          name="namaBelakang"
          required
        />

        <button type="submit">Add Product</button>
        <button type="button" class="cancelButton" (click)="addprod()">
          Cancel
        </button>
      </form>
    </div>
  </div>
</div>
