import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthService } from 'src/services/AuthService.service';
import { Router } from '@angular/router';
import {
  FormBuilder,
  FormGroup,
  FormControl,
  Validators,
} from '@angular/forms';

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css'],
})
export class AdminLoginComponent implements OnInit {
  username: string = '';
  password: string = '';
  isAuthenticated: boolean = false;
  url: string = 'https://652e8c840b8d8ddac0b194c7.mockapi.io/admin/AdminData';

  form!: FormGroup;

  constructor(
    private http: HttpClient,
    private authService: AuthService,
    private router: Router,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit(): void {
    this.form = this.formBuilder.group({
      userName: [
        '',
        [
          Validators.required,
          Validators.minLength(8),
          Validators.maxLength(20),
        ],
      ],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(8),
          Validators.maxLength(20),
        ],
      ],
    });
  }

  onSubmit() {
    this.http.get(this.url).subscribe((data: any) => {
      const foundUser = data.find(
        (user: any) =>
          user.userName === this.username && user.Password === this.password
      );

      if (foundUser) {
        this.authService.login(); // Set authentication status to true.
        console.log('User authenticated');
        // Redirect to the 'prod-list' route using the Angular router.
        this.router.navigate(['/us-list']);
      } else {
        this.authService.logout(); // Set authentication status to false.
        console.log('Authentication failed');
      }
    });
  }
}
<div class="login-container">
  <h2>Admin Login</h2>
  <form (ngSubmit)="onSubmit()" [formGroup]="form">
    <div class="form-group">
      <label for="username">Username:</label>
      <input
        formControlName="userName"
        type="text"
        id="username"
        name="username"
        required
        [(ngModel)]="username"
      />
      <div *ngIf="form.get('userName')?.hasError('required')">
        <span class="error-message">Username is required.</span>
      </div>
      <div *ngIf="form.get('userName')?.hasError('minlength')">
        <span class="error-message"
          >Username must be at least 8 characters long.</span
        >
      </div>
      <div *ngIf="form.get('userName')?.hasError('maxlength')">
        <span class="error-message">Username cannot exceed 20 characters.</span>
      </div>
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input
        formControlName="password"
        type="password"
        id="password"
        name="password"
        required
        [(ngModel)]="password"
      />
    </div>
    <button type="submit">Login</button>
  </form>

  <div *ngIf="isAuthenticated; else failedAuthentication">
    <p>Authentication Successful! Redirecting...</p>
    <!-- You can add your navigation logic here -->
  </div>
  <ng-template #failedAuthentication>
    <p *ngIf="username || password">
      Authentication failed. Please check your credentials.
    </p>
  </ng-template>
</div>
