import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css'],
})
export class AdminLoginComponent {
  username: string = '';
  password: string = '';
  isAuthenticated: boolean = false;

  onSubmit() {
    // In a real application, you would make an HTTP request to a server for authentication.
    // Here, we are just demonstrating a simple mock authentication.
    if (this.username === 'admin' && this.password === 'password') {
      this.isAuthenticated = true;
      console.log('User authenticated');
    } else {
      this.isAuthenticated = false;
      console.log('Authentication failed');
    }
  }
}
<div class="login-container">
  <h2>Admin Login</h2>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="username">Username:</label>
      <input
        type="text"
        id="username"
        name="username"
        required
        [(ngModel)]="username"
      />
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input
        type="password"
        id="password"
        name="password"
        required
        [(ngModel)]="password"
      />
    </div>
    <button type="submit">Login</button>
  </form>

  <div *ngIf="isAuthenticated; else failedAuthentication">
    <p>Authentication Successful! Redirecting...</p>
    <!-- You can add your navigation logic here -->
  </div>
  <ng-template #failedAuthentication>
    <p *ngIf="username || password">
      Authentication failed. Please check your credentials.
    </p>
  </ng-template>
</div>
