import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpCallService } from 'src/services/http.service.';
import { prodDataList } from '../Modals/Modals';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css'],
})
export class UserListComponent {
  // Properti yang diperlukan
  prodData: prodDataList[] = [];
  url: string = 'https://652e8e2a0b8d8ddac0b197f5.mockapi.io/test/List';
  editingprodId: number | null = null;
  editedprod: prodDataList = {} as prodDataList;
  newprod: prodDataList = {} as prodDataList;
  isAddprodFormVisible: boolean = false;

  // Properti untuk transaksi
  transactionType: string = 'Stock In'; // 'Stock In' atau 'Stock Out'
  selectedProducts: prodDataList[] = [];
  transactionQuantity: number = 0;

  // Riwayat transaksi
  transactionHistory: any[] = [];

  constructor(public restApi: HttpCallService, private router: Router) {}

  ngOnInit() {
    this.getData();
  }

  addprod() {
    this.isAddprodFormVisible = !this.isAddprodFormVisible;
  }

  addNewprod() {
    this.restApi.post(this.url, this.newprod).subscribe((response: any) => {
      this.prodData.push(response);
      this.getData();
      this.newprod = {
        id: 0,
        name: '',
        description: '',
        gambar: '',
        category: '',
        stock: 0,
      };
      this.addprod();
    });
  }

  getData() {
    this.restApi.get(this.url).subscribe((data: any) => {
      this.prodData = data;
      console.log(this.prodData);
    });
  }

  editprod(id: number) {
    this.editingprodId = id;
    const prodToEdit = this.prodData.find((prod) => prod.id === id);
    if (prodToEdit !== undefined) {
      this.editedprod = prodToEdit;
    } else {
      null;
    }
  }

  saveprod() {
    this.restApi
      .put(`${this.url}/${this.editingprodId}`, this.editedprod)
      .subscribe(() => {
        this.getData();
        this.editingprodId = null;
      });
  }

  cancelEdit() {
    this.editingprodId = null;
  }

  deleteprod(id: number) {
    const prodIndex = this.prodData.findIndex((u) => u.id === id);
    if (prodIndex !== -1) {
      const prodToDelete = this.prodData[prodIndex];
      this.restApi.delete(`${this.url}/${prodToDelete.id}`).subscribe(() => {
        this.prodData.splice(prodIndex, 1);
        const selectedProductIndex = this.selectedProducts.findIndex(
          (p) => p.id === id
        );
        if (selectedProductIndex !== -1) {
          this.selectedProducts.splice(selectedProductIndex, 1);
        }
      });
    }
  }

  addToTransaction(product: prodDataList) {
    // Memeriksa apakah produk dengan ID yang sama sudah ada dalam transaksi
    const isProductSelected = this.selectedProducts.some(
      (p) => p.id === product.id
    );

    if (isProductSelected) {
      alert('Produk ini sudah ada dalam transaksi.');
    } else {
      this.selectedProducts.push(product);
    }
  }

  removeFromTransaction(product: prodDataList) {
    const index = this.selectedProducts.indexOf(product);
    if (index !== -1) {
      this.selectedProducts.splice(index, 1);
    }
  }

  updateProductStock(productId: number, newStock: number) {
    const productIndex = this.prodData.findIndex((p) => p.id === productId);
    if (productIndex !== -1) {
      this.prodData[productIndex].stock = newStock;
      this.restApi
        .put(`${this.url}/${productId}`, this.prodData[productIndex])
        .subscribe(() => {
          console.log(`Stock for product ${productId} updated to ${newStock}`);
        });
    }
  }

  performTransaction() {
    if (this.selectedProducts.length === 0) {
      alert('Pilih produk sebelum melakukan transaksi.');
      return;
    }

    if (this.transactionType === 'Stock In') {
      for (const product of this.selectedProducts) {
        product.stock += this.transactionQuantity;
        this.updateProductStock(product.id, product.stock);
      }
    } else if (this.transactionType === 'Stock Out') {
      for (const product of this.selectedProducts) {
        if (product.stock < this.transactionQuantity) {
          alert('Jumlah transaksi Stock Out melebihi stok produk.');
          return;
        }
        product.stock -= this.transactionQuantity;
        this.updateProductStock(product.id, product.stock);
      }
    }

    this.transactionHistory.push({
      type: this.transactionType,
      products: this.selectedProducts.map((product) => ({
        id: product.id,
        name: product.name,
      })),
      quantity: this.transactionQuantity,
      date: new Date(),
    });

    this.transactionType = '';
    this.selectedProducts = [];
    this.transactionQuantity = 0;
  }

  getTransactionProducts(transaction: any) {
    return transaction.products.map((p: any) => p.name).join(', ');
  }
}
<app-navbar></app-navbar>

<div class="wrapper-card">
  <button class="add-button" (click)="addprod()">Add</button>

  <!-- Menampilkan menu transaksi -->
  <div class="transaction-menu">
    <h2>Transaction Menu</h2>
    <div>
      <label for="transactionType">Transaction Type:</label>
      <select
        id="transactionType"
        [(ngModel)]="transactionType"
        name="TransactionType"
        required
      >
        <option value="Stock In">Stock In</option>
        <option value="Stock Out">Stock Out</option>
      </select>
    </div>
    <div>
      <label for="transactionQuantity">Transaction Quantity:</label>
      <input
        type="number"
        id="transactionQuantity"
        [(ngModel)]="transactionQuantity"
        name="TransactionQuantity"
        required
      />
    </div>
    <div>
      <label>Selected Products:</label>
      <ul>
        <li *ngFor="let product of selectedProducts">
          {{ product.name }}
          <button (click)="removeFromTransaction(product)">Remove</button>
        </li>
      </ul>
    </div>
    <div>
      <button (click)="performTransaction()">Perform Transaction</button>
    </div>
  </div>

  <!-- Menampilkan riwayat transaksi -->
  <div class="transaction-history">
    <h2>Transaction History</h2>
    <ul>
      <li *ngFor="let transaction of transactionHistory"></li>
      <li *ngFor="let transaction of transactionHistory">
        {{ transaction.type }} - Products:
        {{ getTransactionProducts(transaction) }} - Quantity:
        {{ transaction.quantity }} - Date:
        {{ transaction.date | date : "short" }}
      </li>
    </ul>
  </div>

  <!-- Tampilkan daftar produk -->
  <div *ngFor="let prod of prodData" class="prod-card">
    <div class="prod-details">
      <p>Name: {{ prod.name }}</p>
      <p>Description: {{ prod.description }}</p>
      <img src="{{ prod.gambar }}" alt="gambar" width="200" height="200" />
      <p>category: {{ prod.category }}</p>
      <p>Stock: {{ prod.stock }}</p>
    </div>

    <button class="add-to-transaction-button" (click)="addToTransaction(prod)">
      Add to Transaction
    </button>
    <button class="edit-button" (click)="editprod(prod.id)">Edit</button>
    <button class="delete-button" (click)="deleteprod(prod.id)">Delete</button>
  </div>

  <div *ngIf="editingprodId !== null" class="edit-wrapper">
    <h2>Edit Product</h2>
    <form (submit)="saveprod()">
      <label for="editedName">Name:</label>
      <input
        type="text"
        id="editedName"
        [(ngModel)]="editedprod.name"
        name="Name"
        required
      />

      <label for="editedDescription">Description:</label>
      <input
        type="text"
        id="editedDescription"
        [(ngModel)]="editedprod.description"
        name="Description"
        required
      />

      <label for="editedGambar">Gambar:</label>
      <input
        type="text"
        id="editedGambar"
        [(ngModel)]="editedprod.gambar"
        name="Gambar"
        required
      />

      <label for="editedCategory">Category:</label>
      <input
        type="text"
        id="editedCategory"
        [(ngModel)]="editedprod.category"
        name="Category"
        required
      />

      <button type="submit">Save</button>
      <button (click)="cancelEdit()">Cancel</button>
    </form>
  </div>

  <!-- Add prod Form -->
  <div *ngIf="isAddprodFormVisible" class="add-prod-form-wrapper">
    <div class="add-prod-form">
      <h2>Add Product</h2>
      <form (submit)="addNewprod()">
        <label for="editedName">Name:</label>
        <input
          type="text"
          id="editedName"
          [(ngModel)]="newprod.name"
          name="Name"
          required
        />

        <label for="editedBirthdate">Description:</label>
        <input
          type="text"
          id="editedBirthdate"
          [(ngModel)]="newprod.description"
          name="tanggalLahir"
          required
        />

        <label for="editedName">Gambar:</label>
        <input
          type="text"
          id="editedName"
          [(ngModel)]="newprod.gambar"
          name="Description"
          required
        />

        <label for="editedName">Category:</label>
        <input
          type="text"
          id="editedName"
          [(ngModel)]="newprod.category"
          name="Category"
          required
        />

        <label for="editedName">Stock:</label>
        <input
          type="text"
          id="editedName"
          [(ngModel)]="newprod.stock"
          name="Stock"
          required
        />

        <button type="submit">Add Product</button>
        <button type="button" class="cancelButton" (click)="addprod()">
          Cancel
        </button>
      </form>
    </div>
  </div>
</div>
