import { Component, OnInit } from '@angular/core';
import { HttpCallService } from 'src/services/http.service.';
import { userDataList } from '../Modals/Modals';

@Component({
  selector: 'app-prod-list',
  templateUrl: './prod-list.component.html',
  styleUrls: ['./prod-list.component.css'],
})
export class ProdListComponent implements OnInit {
  constructor(public restApi: HttpCallService) {}

  userData: userDataList[] = [];
  url: string = 'https://652e8c840b8d8ddac0b194c7.mockapi.io/admin/userData';

  ngOnInit() {
    this.restApi.get(this.url).subscribe((data: any) => {
      this.userData = data;
      console.log(this.userData);
    });
  }

  edit(id: number) {
    this.restApi.get(this.url + '/' + id).subscribe((data: any) => {
      this.userData = data;
      console.log(this.userData);
    });
  }

  deleteUser(user: userDataList) {
    const userIndex = this.userData.findIndex((u) => u.id === user.id);
    if (userIndex !== -1) {
      const userToDelete = this.userData[userIndex];
      this.restApi.delete(`${this.url}/${userToDelete.id}`).subscribe(() => {
        this.userData.splice(userIndex, 1);
      });
    }
  }
}
<div class="wrapper-card">
  <div *ngFor="let user of userData" class="user-card">
    <h2>
      {{ user.namaDepan }} <span>{{ user.namaBelakang }}</span>
    </h2>
    <div class="user-details">
      <p>Email: {{ user.email }}</p>
      <p>Jenis Kelamin: {{ user.jenisKelamin }}</p>
    </div>
    <p>Tanggal Lahir: {{ user.tanggalLahir | date }}</p>
    <button class="edit-button" (click)="edit(user.id)">Edit</button>
    <button class="delete-button" (click)="delete(user.id)">Delete</button>
  </div>
</div>
