import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpCallService } from 'src/services/http.service.';
import { catDataList } from '../Modals/Modals';

@Component({
  selector: 'app-cat-list',
  templateUrl: './cat-list.component.html',
  styleUrls: ['./cat-list.component.css'],
})
export class CatListComponent {
  constructor(public restApi: HttpCallService, private router: Router) {}

  catData: catDataList[] = [];
  url: string = 'https://652e8e2a0b8d8ddac0b197f5.mockapi.io/test/Category';

  editingUserId: number | null = null;
  editedUser: catDataList = {} as catDataList;
  newUser: catDataList = {} as catDataList;

  isAddUserFormVisible: boolean = false;

  ngOnInit() {
    this.getData();
  }

  addUser() {
    this.isAddUserFormVisible = !this.isAddUserFormVisible;
  }

  addNewUser() {
    this.restApi.post(this.url, this.newUser).subscribe((response: any) => {
      this.catData.push(response);
      this.getData();
      this.newUser = {
        id: 0,
        name: '',
        description: '',
      };
      this.addUser();
    });
  }

  getData() {
    this.restApi.get(this.url).subscribe((data: any) => {
      this.catData = data;
      console.log(this.catData);
    });
  }

  editUser(id: number) {
    this.editingUserId = id;
    const userToEdit = this.catData.find((user) => user.id === id);
    if (userToEdit !== undefined) {
      this.editedUser = userToEdit;
    } else {
      null;
    }
  }

  saveUser() {
    this.restApi
      .put(`${this.url}/${this.editingUserId}`, this.editedUser)
      .subscribe(() => {
        this.getData();
        this.editingUserId = null;
      });
  }

  cancelEdit() {
    this.editingUserId = null;
  }

  deleteUser(id: number) {
    const userIndex = this.catData.findIndex((u) => u.id === id);
    if (userIndex !== -1) {
      const userToDelete = this.catData[userIndex];
      this.restApi.delete(`${this.url}/${userToDelete.id}`).subscribe(() => {
        this.catData.splice(userIndex, 1);
      });
    }
  }
}
<app-navbar></app-navbar>

<div class="wrapper-card">
  <button class="add-button" (click)="addUser()">Add</button>

  <div *ngFor="let user of catData" class="user-card">
    <div class="user-details">
      <p>Name: {{ user.name }}</p>
      <p>Description: {{ user.description }}</p>
    </div>

    <button class="edit-button" (click)="editUser(user.id)">Edit</button>
    <button class="delete-button" (click)="deleteUser(user.id)">Delete</button>
  </div>

  <div *ngIf="editingUserId !== null" class="edit-wrapper">
    <h2>Edit User</h2>
    <form (submit)="saveUser()">
      <!-- Create form inputs for editing user details -->
      <label for="editedName">Name:</label>
      <input
        type="text"
        id="editedName"
        [(ngModel)]="editedUser.name"
        name="namaDepan"
        required
      />

      <label for="editedBirthdate">Birthdate:</label>
      <input
        type="date"
        id="editedBirthdate"
        [(ngModel)]="editedUser.description"
        name="tanggalLahir"
        required
      />

      <button type="submit">Save</button>
      <button (click)="cancelEdit()">Cancel</button>
    </form>
  </div>

  <!-- Add User Form -->
  <div *ngIf="isAddUserFormVisible" class="add-user-form-wrapper">
    <div class="add-user-form">
      <h2>Add User</h2>
      <form (submit)="addNewUser()">
        <label for="newFirstName">First Name:</label>
        <input
          type="text"
          id="newFirstName"
          [(ngModel)]="newUser.namaDepan"
          name="namaDepan"
          required
        />

        <label for="newLastName">Last Name:</label>
        <input
          type="text"
          id="newLastName"
          [(ngModel)]="newUser.namaBelakang"
          name="namaBelakang"
          required
        />

        <label for="newEmail">Email:</label>
        <input
          type="email"
          id="newEmail"
          [(ngModel)]="newUser.email"
          name="email"
          required
        />

        <label for="newGender">Gender:</label>
        <input
          type="text"
          id="newGender"
          [(ngModel)]="newUser.jenisKelamin"
          name="jenisKelamin"
          required
        />

        <label for="newBirthdate">Birthdate:</label>
        <input
          type="date"
          id="newBirthdate"
          [(ngModel)]="newUser.tanggalLahir"
          name="tanggalLahir"
          required
        />

        <button type="submit">Add User</button>
        <button type="button" class="cancelButton" (click)="addUser()">
          Cancel
        </button>
      </form>
    </div>
  </div>
</div>
